let clienteNit = $('Buscar productos').first().json.query.clientenit 
let sucursalId = $('Buscar productos').first().json.query.sucursalId
const search = $('Buscar productos').first().json.query.search
let consulta = `SELECT SP.idproducto AS "Código", mp.nombreproducto AS "Producto", Familia, Grupo, Marca, Modelo, SP.disponible AS "Disponible", SP.idunidad AS "Unidad", CASE ( SELECT listaprecios FROM matercerosuc WHERE nit = '900339960' AND idsuc = '1' LIMIT 1 ) WHEN 1 THEN mp.lista1 WHEN 2 THEN mp.lista2 WHEN 3 THEN mp.lista3 WHEN 4 THEN mp.lista4 WHEN 5 THEN mp.lista5 WHEN 6 THEN mp.lista6 ELSE NULL END AS "Precio", imp.porcentaje, imp.codimp FROM SPSALDOSINVENTARIO (CURRENT_DATE,'N','','','S','JCORTES') SP INNER JOIN maproductos mp ON SP.idproducto = mp.idproducto INNER JOIN vpareto_producto ON SP.idproducto = vpareto_producto.idproducto INNER JOIN maperfilproducto pf ON mp.idperfil = pf.idperfil INNER JOIN maimpuestos imp ON imp.codimp = pf.ivaventa WHERE SP.SALDO > 0 AND mp.activo = 'S'AND SP.idbodega = '1'`
if(search) {
  consulta += `AND SP.idproducto like '${search.toUpperCase()}%'` 
}

consulta += ` ORDER BY 1, 2`

return  ({ clienteNit, sucursalId, consulta, search })

return {
  vendedor: {
