return { pedido, sucursal };

const pedidoId = $('Insertar pedido en la tabla').first().json.data.insertId
const items = $input.all().map(det => det.json)
const { vendedor, pedido, sucursal } = $('Componer request').first().json
const errorInventario = items.filter(item => item.item.cantidad > item.item.disponible)
return {
  vendedor,
  pedido: {
    ...pedido,
    items,
    id: pedidoId,
    errorInventario
  },
  sucursal,
}

const { cantidad, idProducto, idPedido } = $input.first().json

let consulta = ` SELECT SP.idproducto AS "Código", mp.nombreproducto AS "Producto", Familia, Grupo, Marca, Modelo, SP.disponible AS "Disponible", SP.idunidad, SP.idbodega, CASE ( SELECT listaprecios FROM matercerosuc WHERE nit = '` + $('Componer pedido y sucursal').first().json.sucursal.nit +`' AND idsuc = `+ $('Componer pedido y sucursal').first().json.sucursal.sucursal +` LIMIT 1 ) WHEN 1 THEN mp.lista1 WHEN 2 THEN mp.lista2 WHEN 3 THEN mp.lista3 WHEN 4 THEN mp.lista4 WHEN 5 THEN mp.lista5 WHEN 6 THEN mp.lista6 ELSE NULL END AS "Precio", imp.porcentaje, imp.codimp FROM SPSALDOSINVENTARIO (CURRENT_DATE,'N','','','S','JCORTES') SP INNER JOIN maproductos mp ON SP.idproducto = mp.idproducto INNER JOIN vpareto_producto ON SP.idproducto = vpareto_producto.idproducto INNER JOIN maperfilproducto pf ON mp.idperfil = pf.idperfil INNER JOIN maimpuestos imp ON imp.codimp = pf.ivaventa WHERE SP.SALDO > 0 AND mp.activo = 'S'AND SP.idbodega = '1' AND SP.idproducto = '` + idProducto +`' ORDER BY 1, 2 ` 

return {consulta, cantidad, idProducto, idPedido};

