const peticionPedido = $input.first().json.pedido
const pedido = {
  comentarios: peticionPedido.comentarios,
  sucursal: peticionPedido.sucursal,
  nit: peticionPedido.nit,
  productos: peticionPedido.productos.map(producto => ({
